exports.schemas = "\n  type User{\n    id:ID!\n    phoneNo:String!\n  }\n  \n  input NewUserInput{\n    phoneNo:String!\n  }\n  type Cart{\n    id:ID!\n    user_id:ID!\n    site:String!\n    item:String!\n    image:String\n    unitPrice:Float!\n    description:String!\n    quantity:Int!\n    measurementUnit:String!\n\n  }\n  input NewCartItemInput{\n    user_id:ID!\n    site:String!\n    item:String!\n    image:String\n    unitPrice:Float!\n    description:String!\n    quantity:Int!\n    measurementUnit:String!\n  }\n\n  input UpdateCartInput{\n    quantity:Int!\n  }\n\n\n  type EcommerceAccount{\n    id: ID!\n    email:String!\n    username:String!\n    site:String!\n    password:String!\n    user_id:Int!\n  }\n\n  input NewAccountInput{\n    email:String!\n    username:String!\n    site:String!\n    password:String!\n    user_id:Int!\n  }\n\n  input UpdateAccountInput{\n    email:String\n    username:String\n    password:String\n  }\n\n  type Query{\n    accounts:[EcommerceAccount]\n    accountById(id:ID):EcommerceAccount\n    accountBySite(site: String):EcommerceAccount\n    users:[User]!\n    userByPhoneNo:User!\n    myCart:[Cart]\n    cart:Cart\n  }\n  type Mutation{\n    addAccount(input: NewAccountInput!):EcommerceAccount!\n    updateAccount(id:ID!, input: UpdateAccountInput!):EcommerceAccount!\n    deleteAccount(id:ID!):EcommerceAccount\n    addToCart(input:NewCartItemInput!):Cart!\n    removeFromCart(id:ID!):Cart\n    emptyCart(user_id:ID!):[Cart]\n    updateCart(id:ID!, input:UpdateCartInput):Cart!\n    register(input:NewUserInput):User!\n    removeUser(id:ID!):User\n  }\n  schema {\n    query: Query\n    mutation: Mutation\n  }\n  \n";
